// Commands-List: https://developer.mozilla.org/en-US/docs/Web/API/Document/execCommand

function formatText(event, command, value = null) {
    event.preventDefault();

    document.execCommand("defaultParagraphSeparator", false, "p");
    document.execCommand(command, false, value);

    //getCode();
}

function getCode(editorid) {
    document.getElementById("minirte-code-" + editorid).value = document.getElementById("minirte-editor-" + editorid).innerHTML;
}

function checkLineBreaks(event) {
    if (event.which === 13) {
        event.preventDefault();
    }
}

function formatPaste(event) {
    event.preventDefault();

    var text = event.clipboardData.getData("text/plain");
    var temp = document.createElement("div");
    temp.innerHTML = text;
    text = temp.textContent.replace(/\r?\n|\r/g, "");

    formatText(event, 'insertText', text);
}
